pragma solidity ^0.4.2;

contract Auction{

    struct Auctioner{
        string auctionerName;
        string auactionerDescription;
        string auctionerImage;
    }

    struct Beneficiary{
        //address beneficiary;
        string beneficiaryName;
        string beneficiaryImage;
        string beneficiaryContactLink;
    }

    struct Item{
        string catalog;
        string itemName;
        string itemImage;
    }
    
    Item public auctionItem;
    Auctioner public auctioner;
    Beneficiary public beneficiary;
    address public highestBidder;
    uint public highestBid=0;

    constructor() public {
       
        //Need a better way to do initialize class members

        //beneficiary=Beneficiary(_beneficiary,_beneficiaryName,_beneficiaryImage,_beneficiaryContactLink);
        beneficiary=Beneficiary("Cheif Minister's Relief Fund","https://cdn.downtoearth.org.in/library/large/2018-08-23/0.42716000_1535013850_23.jpg","https://donation.cmdrf.kerala.gov.in/");
        auctioner=Auctioner("Glenn Jacobs","Mayor @Knox County,Tennessee. Former-WWE World & Tag Team Champion","https://pbs.twimg.com/profile_images/734105730484166656/ROt3XWKn_400x400.jpg");
        auctionItem=Item("The iconic Demon Kane Mask, that featured on numerous episodes of Monday night RAW and WrestleMania. Worn by Jacobs when he was Director of Operations or Corporate Kane","Demon Kane Mask","https://pbs.twimg.com/media/Cbts4yzVIAAJrfM.jpg");
        
    }

    event highestBidSubmitted(uint _highestBid,address _highestBidder);

    function bid() payable public{
        if(highestBid<msg.value){
            highestBid=msg.value;
            highestBidder=msg.sender;
            emit highestBidSubmitted(highestBid,highestBidder);
        }
    }

}
